        private void Form1_Load(object sender, EventArgs e)
        {
            Hide();

            //Cargar el valor de tiempo para el temporizador desde la BD 
            int intervalo = 1;
            Configuracion = conexionAPI.ObtenerConfiguracion(repositorioGeneral.ObtenerConfigWebConfig("Intervalo"));

            if (Configuracion.Valor != null)
            {
                if (int.TryParse(Configuracion.Valor, out intervalo))
                    intervalo = int.Parse(Configuracion.Valor);
            }
            webBrowser1.IsWebBrowserContextMenuEnabled = false;
            timer1.Interval = intervalo * 60000;
            timerActualizaciones.Interval = int.Parse(repositorioGeneral.ObtenerConfigWebConfig("Actualizacion")) * 60000;
            timerInventario.Interval = int.Parse(repositorioGeneral.ObtenerConfigWebConfig("Inventario")) * 60000;
            //----------------------------------------------------------

            //Configurar el Formulario----------------------------------
            lblVersion.Text = repositorioGeneral.ObtenerConfigWebConfig("Version");
            sqLite.GuardarBitacora("Se inicio aplicativo de Mensajeria", DateTime.Now);
            toolTip1.SetToolTip(nuevaActualizacion, "Nueva actualización disponible haz clic para instalar");
            toolTip2.SetToolTip(nuevoMensaje, "Tienes mensaje(s) sin leer");

            webBrowser1.Visible = false;
            wbSinMensajes.Visible = false;
            wbSinMensajes.DocumentText = sinMensajes;
            listViewMensajes.Visible = true;
            btnRegresar.Visible = false;
            nuevoMensaje.Visible = false;

            listaAcumulada = conexionAPI.ObtenerMensajesGeneral(Environment.MachineName.ToString(), 0);
            //-----------------------------------------------------------
            
            CheckForIllegalCrossThreadCalls = false;
            if (!backgroundWorker1.IsBusy)
                backgroundWorker1.RunWorkerAsync();

            try
            {
                Inventario.Data.InventarioEquipo.Add();
            }
            catch (Exception)
            {
                return;
            }
        }

