        // Detecta la pantalla en la que está el form y redimensiona
        private void resizeForm()
        {
            Screen[] screens = Screen.AllScreens;
            Screen screen = Screen.FromControl(this);

            int anchoP = screen.WorkingArea.Height;
            double wbA = anchoP - 140;
            int webA = (int)Math.Round(wbA);

            webBrowser1.Size = new Size(400, webA);
            wbSinMensajes.Size = new Size(400, webA);
            listViewMensajes.Size = new Size(400, webA);
            // Heigh 100 para que sea menor al permitido y redimensione
            Height = 100;
        }

        private void linkLabelDashboards_LinkClicked(object sender, LinkLabelLinkClickedEventArgs e)
        {
            Encriptacion.SymCryptography encriptacion = new Encriptacion.SymCryptography();
            encriptacion.Key = encriptacion.GetKey();
            var NombreMaquina = encriptacion.Encrypt(Environment.MachineName.ToString());
            var Codificacado = Encoding.UTF8.GetBytes(NombreMaquina);
            Process.Start(repositorioGeneral.ObtenerConfigWebConfig("SitioDashboards") + Convert.ToBase64String(Codificacado));
            Hide();
        }

