        }

        private void backgroundWorker1_RunWorkerCompleted(object sender, RunWorkerCompletedEventArgs e)
        {
            try
            {
                lblCargando.Visible = false;
                if (listaAcumulada.Count == 0)
                {
                    wbSinMensajes.Visible = true;
                    webBrowser1.Visible = false;
                    listViewMensajes.Visible = false;
                    btnActualizar.Visible = true;
                    btnRegresar.Visible = false;
                }
                else if (listaAcumulada.Count > 0 && webBrowser1.Visible)
                {
                    webBrowser1.Visible = true;
                    wbSinMensajes.Visible = false;
                    btnRegresar.Visible = !Mantenimiento;
                    btnActualizar.Visible = Mantenimiento;
                    listViewMensajes.Visible = false;
                }
                else
                {
                    RecargaLista();
                    webBrowser1.Visible = false;
                    wbSinMensajes.Visible = false;
                    btnRegresar.Visible = false;
                    btnActualizar.Visible = true;
                    listViewMensajes.Visible = true;
                }
            }
            catch (Exception ex)
            {
                sqLite.GuardarBitacora(ex.Message, DateTime.Now);
                return;
            }
        }

        private void CargarMensajes(bool Actualizar)
        {
            try
            {
                string strEquipo = Environment.MachineName;

                // Get Mensajes
                lstMensajesTMP = conexionAPI.ObtenerMensajesGeneral(strEquipo, 0);

                if (lstMensajesTMP != null)
                {
                    if (lstMensajesTMP.Count > 0 && lstMensajesTMP.First().IdMensaje == 0)
                    {
                        if (!Mantenimiento && !Correo)
                        {
                            Mantenimiento = true;
                            lstMensajesTMP = conexionAPI.ObtenerMensajesGeneral(strEquipo, 1);
                            if (lstMensajesTMP.First().Titulo == "Enviado") Correo = true;
                        }

                        listViewMensajes.Visible = false;
                        webBrowser1.DocumentText = enRevision;
                        webBrowser1.Visible = true;
                        return;
                    }
                }

                Mantenimiento = false;
                if (lstMensajesTMP == null)
                {
                    // Si no se pudo, asumo que no hay internet
                    // Aunque podría serun fallo de lógica o del WS
                    listViewMensajes.Visible = false;
                    webBrowser1.DocumentText = sinConexion;
                    webBrowser1.Visible = true;
                    return;
                }

                if (listaAcumulada == null)
                {
                    listaAcumulada = conexionAPI.ObtenerMensajesGeneral(strEquipo, 0);
                    if (listaAcumulada != null) listaAcumulada.OrderByDescending(r => r.IdMensaje);
                }

                if (lstMensajesTMP.Count() > 0)
                {
                    listaAcumulada.RemoveAll(r => r.IdMensaje == 0);
                    foreach (var item in lstMensajesTMP)
                    {
                        var buscaMensaje = listaAcumulada.Where(r => r.IdMensaje == item.IdMensaje);
                        if (buscaMensaje.Count() == 0)
                        {
                            listaAcumulada.Insert(0, new DBMensajes.MensajeNoContent(item.IdMensaje, item.IdArea, item.IdEmpleado, item.Fecha, item.Titulo, item.Estatus, item.IdUsuarioRegistro,
                            item.FechaRegistro, item.TipoMensaje));
                        }
                    }

                    if (!Actualizar)
                    {
                        // Show Notification
                        var contador = lstMensajesTMP.Count(x => x.Estatus == false);
                        if (contador > 0)
                        {
                            notifyIcon1.ShowBalloonTip(3000, "Centro de mensajes CEPADEMRH", "Tienes un nuevo mensaje, da click aquí para abrirlo", ToolTipIcon.Info);
                        }
                    }
                }
                else
                {
                    listaAcumulada.Clear();
                }
            }
            catch (Exception ex)
            {
                sqLite.GuardarBitacora("Error en metodo CargarMensajes. Error: " + ex.Message, DateTime.Now);
            }
        }

        private void ActualizarEstatusMensaje(int idMensaje)
        {
            try
            {
                RegresaUpdate mensaje = conexionAPI.UpdateMensajes(idMensaje);

                if (mensaje.Message.StatusCode == HttpStatusCode.OK)
                    sqLite.GuardarBitacora("Mensaje con ID " + idMensaje + " visto." + mensaje.Message.StatusCode, DateTime.Now);
            }
            catch (Exception ex)
            {
                sqLite.GuardarBitacora("Error en metodo ActualizarEstatusMensaje. Error: " + ex.Message, DateTime.Now);
            }
        }

