        private void btnActualizar_Click(object sender, EventArgs e)
        {
            CargarMensajes(true);
            RecargaLista();
            btnRegresar.Visible = false;

            if (listaAcumulada.Count == 0)
            {
                wbSinMensajes.Visible = true;
