            {
                webBrowser1.Visible = false;
                wbSinMensajes.Visible = false;
                lblCargando.Visible = false;
                listViewMensajes.Visible = true;
            }
        }
        
        // Genera el registro de la aplicación en el Registro de Windows para inciar después de un reinicio
        private static void InstalarRegistroWindows()
        {
            RegistryKey key = Registry.CurrentUser.OpenSubKey(StartupKey, true);
            key.SetValue(StartupValue, Environment.GetFolderPath(Environment.SpecialFolder.Desktop).ToString() + "\\Notificaciones CEPADEMRH.appref-ms");
        }

        private void RecargaLista()
        {
            bool nuevo = false;
            
            listViewMensajes.Clear();
            listViewMensajes.View = View.Details;
            listViewMensajes.FullRowSelect = true;
            listViewMensajes.Columns.Add("Mensajes", 260);
            listViewMensajes.Columns.Add("Fecha", 120);
            listViewMensajes.HeaderStyle = ColumnHeaderStyle.None;

            listViewMensajes.SmallImageList = imageList;

            if (listaAcumulada == null)
                return;

            foreach (var item in listaAcumulada.OrderByDescending(r => r.Fecha))
            {
                int tipoImagen = 0;
                switch (item.TipoMensaje)
                {
                    case 1:
                        tipoImagen = 0;
                        break;
                    case 2:
                        tipoImagen = 1;
                        break;
                    case 3:
                        tipoImagen = 2;
                        break;
                }

                ListViewItem mensaje = new ListViewItem();
                mensaje.UseItemStyleForSubItems = false;
                mensaje.Font = item.Estatus ? new Font(listViewMensajes.Font, FontStyle.Regular) : new Font(listViewMensajes.Font, FontStyle.Bold);
                mensaje.ImageIndex = tipoImagen;
                mensaje.Text = item.Titulo;
                mensaje.Name = item.IdMensaje.ToString();
                mensaje.SubItems.Add(item.Fecha.ToString("dd/MM/yyyy HH:mm"));
                mensaje.SubItems[1].Font = item.Estatus ? new Font("Arial", 10, FontStyle.Regular) : new Font("Arial", 10, FontStyle.Bold);
                if (!item.Estatus && item.IdMensaje > 0)
                    nuevo = true;

                listViewMensajes.Items.Add(mensaje);
                
            }
            nuevoMensaje.Visible = nuevo;
        }

        private void listViewMensajes_SelectedIndexChanged(object sender, EventArgs e)
        {
            try
            {
                if (nuevaActualizacion.Visible)
                {
                    DialogResult result = MessageBox.Show("Es necesario descargar la actulización disponible. \n\n Haz clic en el botón Aceptar para instalarla ahora.", "Notificaciones CEPADEMRH", MessageBoxButtons.OK, MessageBoxIcon.Information);
                    if (result == DialogResult.OK)
                    {
                        nuevaActualizacion_Click(sender, e);
                        return;
                    }
                }

                foreach (ListViewItem item in listViewMensajes.SelectedItems)
                {
                    DBMensajes db = new DBMensajes();
                    var mensaje = db.GetMessage(int.Parse(item.Name));

                    if (EsURL(mensaje.MensajeDescripcion))
                    {
                        webBrowser1.ScriptErrorsSuppressed = true;
                        webBrowser1.Navigate(mensaje.MensajeDescripcion);
                    }
                    else
                    {
                        webBrowser1.ScriptErrorsSuppressed = true;
                        webBrowser1.Navigate(repositorioGeneral.ObtenerConfigWebConfig("DashboardMensaje") + item.Name);
                    }

                    if (int.Parse(item.Name) >= 0)
                    {
                        if (!listaAcumulada.Where(r => r.IdMensaje == int.Parse(item.Name)).Select(r => r.Estatus).FirstOrDefault())
                            ActualizarEstatusMensaje(mensaje.IdMensaje);

                        listaAcumulada.Where(w => w.IdMensaje == int.Parse(item.Name)).ToList().ForEach(s => s.Estatus = true);
                    }
                    RecargaLista();
                }

                btnRegresar.Visible = true;
                btnActualizar.Visible = false;
                listViewMensajes.Visible = false;
                webBrowser1.Visible = true;
            }
            catch (Exception)
            {
                listViewMensajes.Visible = false;
                webBrowser1.DocumentText = sinConexion;
                webBrowser1.Visible = true;
            }
        }

        
        private void btnRegresar_Click(object sender, EventArgs e)
