        }

        private void timerActualizaciones_Tick(object sender, EventArgs e)
        {
            UpdateCheckInfo info = null;

            if (ApplicationDeployment.IsNetworkDeployed)
            {
                ApplicationDeployment ad = ApplicationDeployment.CurrentDeployment;

                try
                {
                    info = ad.CheckForDetailedUpdate();

                }
                catch (DeploymentDownloadException dde)
                {
                    sqLite.GuardarBitacora("La nueva versión de la aplicación no se puede descargar en este momento. \n\nPor favor revisa tu conexión a Internet. Error: " + dde.Message, DateTime.Now);
                    return;
                }
                catch (InvalidDeploymentException ide)
                {
                    sqLite.GuardarBitacora("No se puede comprobar una nueva version de la aplicación. El despliegue ClickOnce esta corrompido. Por favor despliegue nuevamente e intente de nuevo. Error: " + ide.Message, DateTime.Now);
                    return;
                }
                catch (InvalidOperationException ioe)
                {
                    sqLite.GuardarBitacora("La aplicación no puede ser actualizada. No parece ser una aplicación ClickOnce. Error: " + ioe.Message, DateTime.Now);
                    return;
                }

                if (info.UpdateAvailable)
                {
                    nuevaActualizacion.Visible = true;
                    notifyIcon1.ShowBalloonTip(3000, "Centro de mensajes CEPADEMRH", "Hay una actualización disponible.", ToolTipIcon.Info);
                }
            }
        }

        private void nuevaActualizacion_Click(object sender, EventArgs e)
        {
            Boolean doUpdate = true;

            if (doUpdate)
            {
                try
                {
                    if (ApplicationDeployment.IsNetworkDeployed)
                    {
                        ApplicationDeployment ad = ApplicationDeployment.CurrentDeployment;
                        ad.Update();
                        MessageBox.Show("La aplicación se ha actualizado y ahora se reiniciará.", "Notificaciones CEPADEMRH", MessageBoxButtons.OK, MessageBoxIcon.Warning);
                        Application.Restart();
                    }
                }
                catch (DeploymentDownloadException dde)
                {
                    sqLite.GuardarBitacora("No se puede instalar la última versión de la aplicación. \n\nPor favor revisa tu conexión a Internet, o vuelve a intentarlo más tarde. Error: " + dde, DateTime.Now);
                    nuevaActualizacion.Visible = false;
                    return;
                }
            }
        }

