        //Para cerrar la aplicación cuando se cierra sesión windows (Reinicio o Apagado)
        protected override void WndProc(ref System.Windows.Forms.Message m)
        {
            if (m.Msg == WM_QUERYENDSESSION)
            {
                Environment.Exit(0);
            }

            base.WndProc(ref m);
        } 
